<script lang="ts" setup>
import $axios from 'axios'

const file = ref('')
const img = ref<File>()
const fileName = ref('')
const id = ref('')
const quota = ref(0)
const quotaUsed = ref(0)
const loading = ref(false)
const result = ref<Array<{
  anilist: {
    title: {
      native: string
      romaji: string
    }
  }
  filename: string
  similarity: number
  episode?: number
  from: number
  to: number
  video: string
  image: string
}>>([])

onMounted(() => {
  checkTimes()
})

function beforeUpload(_img: File) {
  if (_img.size / 1024 / 1024 >= 10) {
    ElMessage.error('图片大小不能超过10MB!')
    return false
  }
  const reader = new FileReader()
  reader.onload = () => {
    img.value = _img
    fileName.value = _img.name
    file.value = reader.result as string
  }
  reader.readAsDataURL(_img)
  return false
}
async function start() {
  try {
    loading.value = true
    const formData = new FormData()
    formData.append('image', img.value as unknown as Blob)
    const res = await $axios.post('https://api.trace.moe/search?anilistInfo', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    })
    loading.value = false
    await checkTimes()
    if (!res.data.error) {
      result.value = res.data.result || []
    }
  }
  catch (e) {
    loading.value = false
    ElMessage.error('请求失败，请检查网络连接')
  }
}
async function checkTimes() {
  try {
    const res = await $axios.get('https://api.trace.moe/me')
    id.value = res.data.id || ''
    quotaUsed.value = res.data.quotaUsed || 0
    quota.value = res.data.quota || 0
  }
  catch (e) {
    ElMessage.error('请求失败，请检查网络连接')
  }
}
function secondToDate(seconds: number) {
  const h = Math.floor(seconds / 3600)
  const m = Math.floor((seconds / 60 % 60))
  const s = (seconds % 60).toFixed(2)
  return `${h}:${m}:${s}`
}
</script>

<template>
  <a-typography-title :level="3">
    请选择要识别动漫截图
  </a-typography-title>
  <el-upload
    :file-list="[]"
    :show-upload-list="false"
    accept="image/*"
    :before-upload="beforeUpload"
  >
    <el-input
      readonly
      placeholder="点击这里上传图片"
      :value="fileName"
    >
      <template #append>
        <el-button
          block
          :disabled="!file || quota === 0 || quota - quotaUsed <= 0"
          @click.stop="start"
        >
          <span v-if="loading"><span class="i-icon-park-outline-loading" />识别中</span><span
            v-else
          >开始识别</span>
        </el-button>
      </template>
    </el-input>
  </el-upload>
  <a-typography-paragraph>
    <blockquote>
      使用 trace.moe API，有使用次数限制。用户ID：
      <a-typography-text code>
        {{ id }}
      </a-typography-text>
      ，每月次数剩余：
      <a-typography-text code>
        {{ quota }}
      </a-typography-text>
      ，已使用次数：
      <a-typography-text code>
        {{ quotaUsed }}
      </a-typography-text>
    </blockquote>
  </a-typography-paragraph>
  <template v-if="file">
    <el-divider />
    <a-typography-title :level="3">
      预览
    </a-typography-title>
    <el-image
      style="max-width: 100%"
      :src="file"
      fallback="data:image/png;base64,"
    />
  </template>
  <template v-if="result.length > 0">
    <el-divider />
    <a-typography-title :level="3">
      结果
    </a-typography-title>
    <div
      v-for="(item, index) in result"
      :key="index"
    >
      <el-divider />
      <el-descriptions
        :title="item.anilist.title.native + (item.episode ? ` EP#${item.episode}` : '')"
        bordered
        size="small"
        layout="vertical"
      >
        <el-descriptions-item label="罗马音">
          {{ item.anilist.title.romaji }}
        </el-descriptions-item>
        <el-descriptions-item label="文件名">
          {{ item.filename }}
        </el-descriptions-item>
        <el-descriptions-item label="相似度">
          {{ `${(item.similarity * 100).toFixed(2)}%` }}
        </el-descriptions-item>
        <el-descriptions-item label="匹配位置">
          <a-typography-text code>
            {{ secondToDate(item.from) }}
          </a-typography-text>
          ~
          <a-typography-text code>
            {{ secondToDate(item.to) }}
          </a-typography-text>
        </el-descriptions-item>
        <el-descriptions-item
          label="预览"
          :span="3"
        >
          <video
            controls
            style="max-width: 100%"
            :src="item.video"
            :poster="item.image"
          />
        </el-descriptions-item>
      </el-descriptions>
    </div>
  </template>
</template>

<style scoped lang="scss">
:deep(.el-upload) {
  width: 100% !important;
}
</style>
